import React from 'react'
import styled from 'styled-components'
import {useContext,useEffect} from 'react'
import {Mycontext} from '../Context'





const AllContry  = styled.div`
width  : 100%;
height : auto;
z-index : 200;
text-align : center;
background-color :none;
display : flex;
flex-direction : row;
flex-wrap : wrap;
justify-content : center;
margin-bottom : 20px
`


const Headertext = styled.p`
color : black;
font-weight : 800;
font-family : sans-serif;
width  :100%;
text-align :center;
font-size : 30px
`


const Countrycontent = styled.div`
height : 200px;
width : 300px;
border : solid 1px black;
margin-top : 10px;
margin-left   :30px;
margin-bottom : 20px;








@media (max-width: 550px) {
margin-left : 0;
margin-bottom : 15px    
  } 


`  



function Countrys() {


const {countries}  = useContext(Mycontext)




  return (
    <div>
      <AllContry>
        <Headertext>Country</Headertext>
      {

        countries.slice(1,10).map((f) => {
          return (
            <div>
            <Countrycontent>
            <p>{f.capital}</p>
            </Countrycontent>
            </div>
          )
        })
      }



      </AllContry>
    </div>
  )
}


export default Countrys
